mapper.insert:
INSERT INTO MEMBER(
	ID
	,EMAIL
	,PASSWORD
	,NICKNAME
	,REGIST_DATE
	,SALT
)
VALUES (
	MEMBER_ID_SEQ.NEXTVAL
	,#{email}
	,#{password}
	,#{nickname}
	,SYSDATE
	,#{salt}
)

mapper.select:
SELECT NVL(SALT, '')
FROM MEMBER
WHERE EMAIL = #{email}

SELECT	COUNT(ID)
FROM	MEMBER
WHERE 	NICKNAME = #{nickname}

SELECT	COUNT(ID)
FROM	MEMBER
WHERE	EMAIL = #{email}
